import { motion } from "framer-motion";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "../styles/Contact.css";

const Contact = () => {
	const fade = {
		opacity: 1,
		transition: {
			duration: 1.5,
		},
	};

	const verticalLeft = {
		opacity: 1,
		y: 0,
		transition: {
			duration: 1.5,
		},
	};

	const [formData, setFormData] = useState({
		name: "",
		email: "",
		message: "",
	});
	const [isSubmitting, setIsSubmitting] = useState(false);

	const handleChange = (e) => {
		const { name, value } = e.target;
		setFormData((prev) => ({
			...prev,
			[name]: value,
		}));
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		setIsSubmitting(true);

		const form = e.target;
		const formDataToSubmit = new FormData(form);

		fetch("/", {
			method: "POST",
			headers: { "Content-Type": "application/x-www-form-urlencoded" },
			body: new URLSearchParams(formDataToSubmit).toString(),
		})
			.then(() => {
				toast.success("Thanks for your message! I'll get back to you soon.", {
					position: "top-left",
					autoClose: 2000,
					hideProgressBar: false,
					closeOnClick: true,
					pauseOnHover: true,
					draggable: true,
				});
				setFormData({ name: "", email: "", message: "" });
				setIsSubmitting(false);
			})
			.catch((error) => {
				toast.error("Submission failed. Please try again.", {
					position: "top-left",
					autoClose: 2000,
					hideProgressBar: false,
					closeOnClick: true,
					pauseOnHover: true,
					draggable: true,
				});
				console.error(error);
				setIsSubmitting(false);
			});
	};

	return (
		<>
			<div className='contact' id='contact'>
				<div className='container'>
					<motion.div
						className='heading'
						initial={{ opacity: 0 }}
						whileInView={fade}
						viewport={{ once: true }}>
						<p className='heading-sub-text'>Hire Me</p>
						<p className='heading-text'>Get in Touch</p>
					</motion.div>
					<div className='contact-box'>
						<motion.div
							className='left-box'
							initial={{ opacity: 0, y: "-50px" }}
							whileInView={verticalLeft}>
							<div className='contact-heading'>
								<p>
									I‚Äôm interested in freelance opportunities ‚Äì especially
									ambitious or large projects. However, if you have other
									request or question, don‚Äôt hesitate to use the form
								</p>
							</div>
							<div className='contact-hello'>
								<p>Say Hello</p>
								<Link
									className='hello-links'
									to='//wa.me/+2348060860565'
									target='_blank'>
									wa.me/pappyjay23
								</Link>
								<a
									className='hello-links'
									href='mailto:pjinadu02@gmail.com'
									target='_blank'
									rel='noreferrer'>
									pjinadu02@gmail.com
								</a>
							</div>
						</motion.div>
						<motion.div
							className='right-box'
							initial={{ opacity: 0, y: "50px" }}
							whileInView={verticalLeft}>
							<form
								name='contact-form'
								method='POST'
								data-netlify='true'
								netlify-honeypot='bot-field'
								action='POST'
								onSubmit={handleSubmit}>
								<input type='hidden' name='form-name' value='contact-form' />
								<input type='hidden' name='bot-field' />

								<div className='form-top'>
									<div className='name'>
										<label htmlFor='name'>Your Name</label>
										<input
											type='text'
											name='name'
											id='name'
											value={formData.name}
											onChange={handleChange}
											placeholder='Enter your name'
											required
										/>
									</div>

									<div className='email'>
										<label htmlFor='email'>Your Email</label>
										<input
											type='email'
											name='email'
											id='email'
											value={formData.email}
											onChange={handleChange}
											placeholder='Enter your email address'
											required
										/>
									</div>
								</div>

								<div className='form-mid'>
									<div className='message'>
										<label htmlFor='message'>Your message</label>
										<textarea
											type='text'
											name='message'
											id='message'
											value={formData.message}
											onChange={handleChange}
											placeholder='Hi, I think I need you to work on this particular product. Reach out as soon as you can'
											required></textarea>
									</div>
								</div>

								<div className='form-btn'>
									<button
										type='submit'
										disabled={isSubmitting}
										className='hero-contact'>
										{isSubmitting ? "Sending..." : "Send Message"}
									</button>
								</div>
							</form>
						</motion.div>
					</div>
				</div>
			</div>
			<ToastContainer />
		</>
	);
};

export default Contact;

