}

.Toastify__container {
    z-index: 9999 !important;
}

.Toastify__toast-container {
    z-index: 9999 !important;
}

body {
	overflow-x: hidden;
  background: var(--section-bgd);
}

:root {
	--nav-grey: #a8a9b4;
	--nav-deep-grey: #94959b;
	--nav-blue: #149ddd;
	--nav-blue-shade: #0e668f;
	--card-blue-shade: #112240;
	--nav-deep-blue: #2c2f3f;
	--nav-bgd: #040b14;
	--white: #fff;
	--soft-white: #ccd6f6;
	--navy: #0a192f;
	--light-blue: rgb(156, 183, 255);
	--section-bgd: #00021a;
}

/* General styles */
.about,
.skills,
.services,
.works,
.contact {
	background-color: var(--section-bgd);
	color: var(--nav-grey);
	width: 100%;
}

.container {
	padding: 4rem;
	max-width: 1200px;
	margin: 0 auto;
}

.loader {
	background-color: #00021a;
	height: 100vh;
	width: 100vw;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	overflow: hidden;
}

.loader-icon {
	color: #fff;
	height: 100;
	width: 50;
}

/*  Load animations */
.svg-wrapper {
	position: relative;
}

@keyframes pageLoad {
	0% {
		stroke-dasharray: 140 540;
		stroke-dashoffset: -474;
		stroke-width: 8px;
	}
	100% {
		stroke-width: 2px;
		stroke-dashoffset: 0;
		stroke-dasharray: 760;
	}
}
.shape {
	stroke-dasharray: 140 540;
	stroke-dashoffset: -474;
	stroke-width: 8px;
	fill: transparent;
	stroke: #149ddd;
	border: 5px solid black;
	animation: 3s ease 0s 1 pageLoad;
}
.text {
	font-size: 16px;
	text-transform: uppercase;
	line-height: 32px;
	letter-spacing: 8px;
	color: #fff;
	position: absolute;
	left: 20px;
	top: 10px;
	width: 100%;
	transform: translate(-50% -50%);
}

@media screen and (max-width: 700px) {
	.container {
		padding: 3rem;
	}
}

@media screen and (max-width: 500px) {
	.text {
		font-size: 13px;
		position: absolute;
		left: 40px;
		top: 10px;
	}
	.container {
		padding: 1rem;
	}
}

